sum by(stream_id, direction) (
  sum_over_time({container=~"live-video-to-video.*"} 
    |~ `str_\w+`
    |~ `Input FPS|Output FPS` 
    | regexp `str_(?P<stream_id>\w+).*FPS: (?P<fps>.*$)` 
    | label_format direction="{{ if contains `Input FPS` __line__ }}input{{ else }}output{{ end }}" 
    | unwrap fps [$__auto]
  )
)

avg(
  (
    avg_over_time({container=~"live-video-to-video_streamdiffusion.+", node_name=~".+"} |= "Output FPS" | pattern "<_>Output FPS: <fps>" | unwrap fps [1m]) by (node_name)
  )
  and
  (
    count(avg_over_time({container=~"live-video-to-video_streamdiffusion.+", node_name=~".+"} |= "Output FPS" | pattern "<_>Output FPS: <fps>" | unwrap fps [1m]) by (node_name, container)) by (node_name) == 1
  )
)

